let start=todatetime(unixtime_milliseconds_todatetime($__from + 7200000));
let end=todatetime(unixtime_milliseconds_todatetime($__to + + 7200000));
let _step=$Step;
FAULT_CODE
| where insertedTimestamp between (start .. end)
| where faultCode == "PPT_SYSTEM_ERROR"
| make-series count() default=0 on insertedTimestamp in range(todatetime(unixtime_milliseconds_todatetime($__from + 7200000)), todatetime(unixtime_milliseconds_todatetime($__to + 7200000)), _step)
| mvexpand count_, insertedTimestamp
| extend insertedTimestamp=datetime_add("hour", -2, todatetime(insertedTimestamp))
| extend insertedTimestamp=todatetime(insertedTimestamp)
| extend count_=toint(count_)
| project insertedTimestamp, count_
| sort by insertedTimestamp asc
